mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_REGISTER_DATE, MEMBER_ROLE FROM ( SELECT ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_REGISTER_DATE, MEMBER_ROLE FROM TBL_MEMBER WHERE MEMBER_BLOCKED = 0 ORDER BY MEMBER_REGISTER_DATE DESC )
WHERE ROWNUM <= #{pagination.page} * #{pagination.rowCount} -- 원하는 범위의 끝 값
) WHERE RN > (#{pagination.page} - 1) * #{pagination.rowCount} -- 원하는 범위의 시작 값

SELECT * FROM ( SELECT ROWNUM RN, ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_REGISTER_DATE, MEMBER_ROLE FROM ( SELECT ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_REGISTER_DATE, MEMBER_ROLE FROM TBL_MEMBER WHERE MEMBER_ROLE = 0 AND MEMBER_BLOCKED = 0 ORDER BY MEMBER_REGISTER_DATE DESC )
WHERE ROWNUM <= #{pagination.page} * #{pagination.rowCount} -- 원하는 범위의 끝 값
) WHERE RN > (#{pagination.page} - 1) * #{pagination.rowCount} -- 원하는 범위의 시작 값

SELECT * FROM ( SELECT ROWNUM RN, ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_REGISTER_DATE, MEMBER_ROLE FROM ( SELECT ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_REGISTER_DATE, MEMBER_ROLE FROM TBL_MEMBER WHERE MEMBER_ROLE = 1 AND MEMBER_BLOCKED = 0 ORDER BY MEMBER_REGISTER_DATE DESC )
WHERE ROWNUM <= #{pagination.page} * #{pagination.rowCount} -- 원하는 범위의 끝 값
) WHERE RN > (#{pagination.page} - 1) * #{pagination.rowCount} -- 원하는 범위의 시작 값

SELECT * FROM ( SELECT ROWNUM RN, ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_ROLE, MEMBER_REPORT_NO FROM ( SELECT ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_ROLE, MEMBER_REPORT_NO FROM TBL_MEMBER WHERE MEMBER_BLOCKED = 0 AND MEMBER_REPORT_NO > 0 ORDER BY MEMBER_REPORT_NO DESC )
WHERE ROWNUM <= #{pagination.page} * #{pagination.rowCount} -- 원하는 범위의 끝 값
) WHERE RN > (#{pagination.page} - 1) * #{pagination.rowCount} -- 원하는 범위의 시작 값

SELECT * FROM ( SELECT ROWNUM RN, ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_REGISTER_DATE, MEMBER_UPDATE_DATE, MEMBER_ROLE, MEMBER_BLOCKED FROM ( SELECT ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_REGISTER_DATE, MEMBER_UPDATE_DATE, MEMBER_ROLE, MEMBER_BLOCKED FROM TBL_MEMBER WHERE MEMBER_BLOCKED = 1 ORDER BY MEMBER_UPDATE_DATE DESC )
WHERE ROWNUM <= #{pagination.page} * #{pagination.rowCount} -- 원하는 범위의 끝 값
) WHERE RN > (#{pagination.page} - 1) * #{pagination.rowCount} -- 원하는 범위의 시작 값
