SELECT S.ID, STUDY_TITLE, STUDY_CONTENTS, STUDY_REGISTER_DATE, STUDY_UPDATE_DATE, S.MEMBER_ID, MEMBER_NAME FROM ( SELECT ROWNUM R, S.ID, STUDY_TITLE, STUDY_CONTENTS, STUDY_REGISTER_DATE, STUDY_UPDATE_DATE, S.MEMBER_ID, MEMBER_NAME FROM( SELECT S.ID, STUDY_TITLE, STUDY_CONTENTS, STUDY_REGISTER_DATE, STUDY_UPDATE_DATE, S.MEMBER_ID, MEMBER_NAME FROM TBL_MEMBER JOIN TBL_STUDY ON M.ID = S.MEMBER_ID ORDER BY DESC)P
WHERE ROWNUM <= #{pagination.page} * #{pagination.rowCount}
s
) P WHERE R > (#{pagination.page} - 1) * #{pagination.rowCount}
